    private void jButtonWriteInFileMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButtonWriteInFileMouseClicked
       String s = textAreaMessage.getText();
        try {
               RandomAccessFile file = new RandomAccessFile(new File(path),"rw");
               BufferedWriter out = new BufferedWriter(new FileWriter(path)); //  создаём буферезированный поток
               out.write(""); // очищаем, перезаписав поверх пустую строку
               out.close(); // закрываем
               file.writeChars(s);
                 
            }
        catch (FileNotFoundException e) {} 
        catch (IOException e) {}
        textAreaMessage.setText("");  
    }//GEN-LAST:event_jButtonWriteInFileMouseClicked

