    private void jButtonEncryptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonEncryptActionPerformed
        String s = textAreaMessage.getText();
        String code = "";
        if(lz77)
        {
            LZ77 lz = new LZ77();
            code = lz.Encrypt(s);
        }
        else
        {
            LZ78 lz = new LZ78();
            code = lz.Encrypt(s);
        }
        textAreaMessage.setText(code);
        double coef = (double)s.length()/(double)code.length();
        jTextField1.setText(Double.toString(coef));
        
    }//GEN-LAST:event_jButtonEncryptActionPerformed

