    private void jButtonDecryptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonDecryptActionPerformed
        String s = textAreaMessage.getText();
        String message = "";
        if(lz77)
        {
            LZ77 lz = new LZ77();
            message = lz.Decrypt(s);
        }
        else
        {
            LZ78 lz = new LZ78();
            message = lz.Decrypt(s);
        }
        textAreaMessage.setText(message);
    }//GEN-LAST:event_jButtonDecryptActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
         String s = "";
        try {
               RandomAccessFile file = new RandomAccessFile(new File(path),"rw");
               
               for(int i=0;i<file.length()/2;i++)
               {
                   char c = file.readChar();
                   s += c;
               }
               textAreaMessage.setText(s);
            }
        catch (FileNotFoundException e) {} 
        catch (IOException e) {}
    }//GEN-LAST:event_jButton1ActionPerformed


